statements.statement:

						SELECT M.COMPANY_CD
						      ,M.REQUEST_NO
						      ,M.REQUEST_TYPE
						      ,NVL2(M.MATER_NM_EN,M.MATER_NM_LO,'') MATER_NM
						      ,M.MATER_CD
						      ,M.MATER_TYPE
						      ,M.MATER_GROUP
						      ,M.UNIT
						      ,M.STATUS
						      ,M.CANCEL_MSG
						      ,M.SAP_MSG
						  FROM TB_MATER_REQUEST M
						 WHERE M.COMPANY_CD = ${companyCd}
						 {#1}
						 {#2}
						 {#3}
						 {#4}
						 {#5}
						 ORDER BY M.REQUEST_NO



        	 AND M.REGDATE >= ${fromDate} 



        	 AND M.REGDATE <= ${toDate} 



           AND M.MATER_NM_EN LIKE '%' || ${materNm} || '%' 



           AND M.STATUS   = ${status} 



           AND M.REQUEST_TYPE   = ${requestType}


UPDATE TB_MATER_REQUEST
   SET MATER_NM_EN = ${materNm}
      ,MATER_NM_LO = ${materNm}
      ,MATER_CD    = ${materCd}
      ,UNIT        = ${unit}
      ,MODID       = ${userId}
      ,MODDATE     = TO_CHAR(SYSDATE,'YYYYMMDD')
      ,MODTIME     = TO_CHAR(SYSDATE,'HH24MISS')
 WHERE REQUEST_NO = ${requestNo}
   AND STATUS = '01'


DELETE FROM TB_MATER_REQUEST
 WHERE REQUEST_NO = ${requestNo}
   AND STATUS = '01'
