statements.statement:
SELECT DISTINCT 'F' chk ,a.po_no ,a.regdate ,a.deli_date ,a.currency_cd ,a.pay_term ,a.discount_amt ,a.sap_po_no ,a.vend_cd ,DECODE(${lang},'kr',c.vend_nm_kr,'en',c.vend_nm_en, c.vend_nm_lo) vend_nm ,a.pur_dept_cd ,f_getcommname('COMM',${companyCd},${lang},'2001',a.PUR_DEPT_CD) pur_dept_name ,a.deli_loct ,f_getcommname('COMM',${companyCd},${lang},'2005',a.DELI_LOCT) delivy_pla_name ,a.attr2 po_type FROM tb_po_master a ,tb_po_detail b ,tb_vend_master c WHERE a.company_cd = c.company_cd AND a.VEND_CD = c.vend_cd AND a.company_cd = ${companyCd} AND a.status = '03' --AND 0 = (SELECT COUNT(*) cnt FROM tb_po_detail i WHERE i.po_no = a.po_no AND i.company_cd = a.company_cd AND NVL(i.receipt_cls_yn, 'N') = 'N') AND a.iv_close = 'N' {#1} {#2} {#3} ORDER BY A.PO_NO
AND a.TRANS_DATE >= ${fromDate}
and TRANS_DATE <= ${toDate}
AND A.PO_NO like '%' || ${poNo} || '%'

SELECT 'F' as chk ,T.COMPANY_CD ,T.PO_NO ,T.IV_SEQ ,WT_SEQ ,WT_TYPE ,WT_CD ,WT_RATE ,WT_BASE_AMT ,WT_TAX_AMT
FROM TB_IV_MASTER M, TB_IV_WT T WHERE M.company_cd = T.company_cd AND M.po_no = T.po_no AND M.iv_seq = T.iv_seq AND M.company_cd = ${companyCd} AND T.iv_seq = ${seq} AND T.PO_NO = ${poNo}
ORDER BY WT_SEQ


		SELECT NVL(MAX(TO_NUMBER(IV_SEQ)),0)+1 as IV_SEQ
		  FROM TB_IV_MASTER
  	    WHERE PO_NO = ${poNo}


	UPDATE tb_po_master
  	SET IV_CLOSE  = ${stat}
        ,MODID    = ${userId}
	    ,MODDATE  = TO_CHAR(SYSDATE,'YYYYMMDD')
	    ,MODTIME  = TO_CHAR(SYSDATE,'HH24MISS')		
 	WHERE COMPANY_CD = ${companyCd}
	  AND PO_NO      = ${poNo}


        	UPDATE TB_IV_MASTER
        	SET SAP_GR_DO_NO = ${sapGrDoNo}
				,SAP_IV_DO_NO = ${sapIvDoNo}
				,SEND_STAT = ${sendStat}
				,SEND_MSG = ${sendMsg}
				,SEND_DATE = TO_CHAR(SYSDATE, 'YYYYMMDD')
				,STATUS = ${sendStat}
				,MODID = ${userId}
				,MODDATE = TO_CHAR(SYSDATE,'YYYYMMDD')
				,MODTIME  = TO_CHAR(SYSDATE,'HH24MISS')				
			WHERE COMPANY_CD = ${companyCd}
				AND PO_NO = ${poNo}
				AND IV_SEQ = ${ivSeq}


        	UPDATE TB_IV_MASTER
        	SET SAP_CANCEL_GR_DO_NO = ${sapGrDoNo}
				,SAP_CANCEL_IV_DO_NO = ${sapIvDoNo}
				,CANCEL_STAT = ${sendStat}
				,CANCEL_MSG = ${sendMsg}
				,CANCEL_DATE = TO_CHAR(SYSDATE, 'YYYYMMDD')
				,STATUS = ${sendStat}
				,MODID = ${userId}
				,MODDATE = TO_CHAR(SYSDATE,'YYYYMMDD')
				,MODTIME  = TO_CHAR(SYSDATE,'HH24MISS')				
			WHERE COMPANY_CD = ${companyCd}
				AND PO_NO = ${poNo}
				AND IV_SEQ = ${ivSeq}


            UPDATE tb_po_detail
			   SET receipt_qty = receipt_qty  + TO_NUMBER(${receiptQty})
			       ,receipt_cls_yn = CASE 
			       							WHEN QTY = RECEIPT_QTY + TO_NUMBER(${receiptQty}) 
			       								THEN 'Y' 
			       							ELSE 'N' 
			       						 END
  			       ,modid      = ${userId}    
			       ,moddate   = TO_CHAR(SYSDATE, 'YYYYMMDD')
			       ,modtime   = TO_CHAR(SYSDATE, 'HH24MISS')
			 WHERE company_cd = ${companyCd}
			   AND po_no = ${poNo}
			   AND po_seq = ${poSeq}


            UPDATE tb_po_detail
			   SET receipt_cls_yn = ${receiptClsYn}
  			       ,modid      = ${userId}    
			       ,moddate   = TO_CHAR(SYSDATE, 'YYYYMMDD')
			       ,modtime   = TO_CHAR(SYSDATE, 'HH24MISS')
			 WHERE company_cd = ${companyCd}
			   AND po_no = ${poNo}
			   AND po_seq = ${poSeq}


            UPDATE tb_po_detail
			   SET 
			       modid      = ${userId}    
			       ,moddate   = TO_CHAR(SYSDATE, 'YYYYMMDD')
			       ,modtime   = TO_CHAR(SYSDATE, 'HH24MISS')
			 WHERE company_cd = ${companyCd}
			   AND po_no = ${poNo}
			   AND po_seq = ${itemSeq}
