statements.statement:
SELECT 'F' chk ,a.po_no ,a.reference_no ,b.po_seq ,a.sap_po_no ,a.vend_cd ,DECODE(${lang},'kr',c.vend_nm_kr,'en',c.vend_nm_en, c.vend_nm_lo) vend_nm ,b.mater_cd ,DECODE(${lang},'kr',d.mater_nm_kr,'en',d.mater_nm_en, d.mater_nm_en) mater_nm ,b.model_nm ,a.pur_dept_cd ,f_getcommname('COMM',${companyCd},${lang},'2001',a.PUR_DEPT_CD) pur_dept_name ,a.deli_loct ,f_getcommname('COMM',${companyCd},${lang},'2005',a.DELI_LOCT) delivy_pla_name ,d.unit ,b.qty ,NVL(b.RECEIPT_QTY, 0) pre_qty ,TO_NUMBER(b.qty) - TO_NUMBER(NVL(b.RECEIPT_QTY, 0)) rec_qty ,NVL(b.RECEIPT_CLS_YN, 'N') cls_yn FROM tb_po_master a ,tb_po_detail b ,tb_vend_master c ,tb_mater_info d WHERE a.company_cd = b.company_cd AND a.po_no = b.po_no AND a.company_cd = c.company_cd AND a.vend_cd = c.vend_cd AND b.company_cd = d.company_cd AND b.mater_cd = d.mater_cd AND d.useyn = 'Y' AND a.company_cd = ${companyCd} AND a.status = '03' AND NVL(b.receipt_cls_yn, 'N') = 'N' {#1} {#2} {#3} ORDER BY A.PO_NO
AND a.TRANS_DATE BETWEEN ${srtDate} AND ${endDate}
AND DECODE(${lang},'kr',c.vend_nm_kr,'en',UPPER(c.vend_nm_en), UPPER(c.vend_nm_lo)) LIKE '%'||UPPER(${sVendor})||'%'
AND A.DELI_LOCT = ${deptCd}


          SELECT NVL(MAX(TO_NUMBER(RECEIPT_SEQ) + 1), 1) RECNO
                 ,TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS') XMLDOCNO 
            FROM TB_RECEIPT_MASTER
			  WHERE COMPANY_CD = ${companyCd}
             AND PO_NO = ${poNo}


            UPDATE tb_po_detail
			   SET receipt_qty = receipt_qty + TO_NUMBER(${recQty})
			       ,receipt_cls_yn = DECODE(receipt_qty + TO_NUMBER(${recQty}),qty,'Y', 'N') 
  			       ,modid      = ${userId}    
			       ,moddate   = TO_CHAR(SYSDATE, 'YYYYMMDD')
			       ,modtime   = TO_CHAR(SYSDATE, 'HH24MISS')
			 WHERE company_cd = ${companyCd}
			   AND po_no = ${poNo}
			   AND po_seq = ${poSeq}


            UPDATE tb_po_detail
			   SET receipt_qty = receipt_qty - TO_NUMBER(${receiptQty})
			       ,receipt_cls_yn = 'N'
			       ,modid      = ${userId}    
			       ,moddate   = TO_CHAR(SYSDATE, 'YYYYMMDD')
			       ,modtime   = TO_CHAR(SYSDATE, 'HH24MISS')
			 WHERE company_cd = ${companyCd}
			   AND po_no = ${poNo}
			   AND po_seq = ${poSeq}


            UPDATE TB_RECEIPT_MASTER
			   SET STATUS = ${recStat}
					,sap_cancel_receipt_no =${returnSapSeq} 
			        ,sap_if_status = ${returnStatus}
			        ,sap_rtn_msg  = ${returnMsg}
			        ,MODID  = 	${userId}
				    ,MODDATE = TO_CHAR(SYSDATE, 'YYYYMMDD')
				    ,MODTIME =  TO_CHAR(SYSDATE, 'HH24MISS')

			 WHERE company_cd = ${companyCd}
			   AND po_no = ${poNo}
			   AND receipt_seq = ${receiptSeq}


			SELECT 
				   'F' as chk
				  ,MA.COMPANY_CD
		          ,MA.PO_NO
		          ,DE.PR_NO
		          ,RECEIPT_SEQ
		          ,MA.SAP_PO_NO
		          ,MA.PUR_DEPT_CD
		          ,MA.VEND_CD
		          ,MA.DELI_LOCT
		          ,RECEIPT_DATE
		          ,POSTING_DATE
		          ,MA.STATUS
		          ,MA.TRANS_DATE
		          ,MA.TRANS_TIME
		          ,MA.PROG_STATUS_DATE
		          ,MA.PROG_STATUS_TIME
		          ,SAP_RECEIPT_NO
		          ,SAP_CANCEL_RECEIPT_NO
		          ,MA.SAP_IF_STATUS
		          ,MA.SAP_RTN_MSG
			FROM TB_RECEIPT_MASTER MA, TB_PO_MASTER DE
			WHERE MA.COMPANY_CD =  DE.COMPANY_CD
			AND   MA.PO_NO = DE.PO_NO
			AND   MA.COMPANY_CD = ${companyCd}
			{#1}
			{#2}
			{#3}
			{#4}
			{#5}
			{#6}
			ORDER BY  MA.PO_NO, TO_NUMBER(RECEIPT_SEQ)


		       AND MA.DELI_LOCT =  ${issue_loc} 


		      AND MA.STATUS =  ${status} 


		       AND MA.VEND_CD = ${vendor}


		       AND MA.PO_NO = ${schPoNo}


             		and MA.POSTING_DATE	>= ${postingDateFrom}



             		and MA.POSTING_DATE	<= ${postingDateTo}

SELECT M.COMPANY_CD ,M.PO_NO ,M.RECEIPT_SEQ ,SAP_PO_NO ,PUR_DEPT_CD ,VEND_CD ,DELI_LOCT ,RECEIPT_DATE ,POSTING_DATE ,STATUS ,TRANS_DATE ,TRANS_TIME ,PROG_STATUS_DATE ,PROG_STATUS_TIME ,SAP_RECEIPT_NO ,SAP_CANCEL_RECEIPT_NO ,SAP_IF_STATUS ,SAP_RTN_MSG , PO_SEQ ,MATER_CD ,(SELECT MATER_NM_EN FROM TB_MATER_INFO M WHERE M.MATER_CD = D.MATER_CD) AS MATER_NM ,(SELECT ATTR1 FROM TB_MATER_INFO M WHERE M.MATER_CD = D.MATER_CD) AS MODEL_NM ,UNIT ,RECEIPT_QTY FROM TB_RECEIPT_DETAIL D, TB_RECEIPT_MASTER M WHERE D.COMPANY_CD = ${companyCd} AND d.company_cd = m.company_cd AND d.po_no = m.po_no AND d.receipt_seq = m.receipt_seq {#1} {#2} {#3} {#4} {#5} {#6} ORDER BY PO_NO, TO_NUMBER(RECEIPT_SEQ)
AND DELI_LOCT = ${issueloc}
AND STATUS = ${status}
AND VEND_CD = ${vendor}
AND M.PO_NO = ${schPoNo}
and POSTING_DATE >= ${postingDateFrom}
and POSTING_DATE <= ${postingDateTo}
