statements.statement:

UPDATE TB_ISSUE_LIST
SET STATUS = '04'			-- SAP Cancel 요청
     ,MODID  = 	${userId}
	 ,MODDATE = TO_CHAR(SYSDATE, 'YYYYMMDD')
	 ,MODTIME =  TO_CHAR(SYSDATE, 'HH24MISS')
WHERE COMPANY_CD = ${companyCd} 
	AND  ISSUE_NO  = ${issueNo} 
	AND  MATER_CD = ${materCd}

SELECT 'F' as chk ,COMPANY_CD ,ISSUE_NO ,MATER_CD ,(SELECT MATER_NM_EN FROM TB_MATER_INFO M WHERE M.MATER_CD = TB_ISSUE_LIST.MATER_CD and company_cd = ${companyCd} ) AS MATER_NM ,UNIT ,ISSUE_QTY ,ISSUE_DATE ,ISSUE_LOC ,ISSUE_DESC ,COST_CENTER ,AREA_CD ,DIV_CD ,BLOCK_CD ,BLOCK_CD2 ,YEAR_CD ,TM_TBM ,POSTING_DATE ,WERKS AS PLANT_CD ,SAP_DOC_NO ,SAP_CANCEL_DOC_NO ,STATUS ,SAP_RTN_MSG ,ATTR1 -- Vendor ,ATTR2 -- Description FROM TB_ISSUE_LIST WHERE COMPANY_CD = ${companyCd} {#1} {#2} {#3} {#4} {#5} ORDER BY ISSUE_NO DESC
and ISSUE_LOC = ${issue_loc}
and MATER_CD = ${materCd}
and STATUS = ${status}
and POSTING_DATE >= ${posting_date_from}
and POSTING_DATE <= ${posting_date_to}
