statements.statement:

 		 DELETE tb_po_master			
		  WHERE company_cd        =     ${companyCd}
		    AND po_no             =     ${poNo}


DELETE lgiusf.tb_po_detail          
 WHERE company_cd     = ${companyCd}
   AND po_no          = ${poNo}   
   AND po_seq         = ${poSeq}


DELETE lgiusf.tb_po_detail          
 WHERE company_cd     = ${companyCd}
   AND po_no          = ${poNo}


DELETE lgiusf.tb_vend_detail          
 WHERE company_cd  =        ${companyCd} 
   AND vend_cd     =        ${vendCd}    
   AND vend_seq    =        ${vendSeq}


UPDATE tb_po_master
   SET email_send_date   =      TO_CHAR(SYSDATE,'YYYYMMDD')			        
  WHERE company_cd       =     ${companyCd}
    AND po_no            =     ${poNo}


UPDATE lgiusf.tb_po_detail
   SET sap_price  = ${rSapPrice}
 WHERE company_cd = ${rCompanyCd}
   AND po_no      = ${rPoNo}
   AND po_seq     = ${rPoSeq}


            MERGE INTO tb_po_tax pt
                  USING ( SELECT ${companyCd}     AS companyCd
                                ,${poNo}          AS poNo
                                ,${taxSeq}         AS taxSeq 
                           FROM DUAL
                         ) temp
                     ON (
                         pt.company_cd    = temp.companyCd
                         AND pt.po_no     = temp.poNo
                         AND pt.tax_seq    = temp.taxSeq                        
                         )
            WHEN MATCHED THEN
							UPDATE SET pt.wt_type          = ${wtType} 
									      ,pt.wt_cd            = ${wtCd}
									      ,pt.wt_rate          = ${wtRate}
									      ,pt.wt_base_amt      = ${wtBaseAmt}
									      ,pt.wt_tax_amt       = ${wtTaxAmt} 
									      ,pt.attr1            = ${attr1}
									      ,pt.attr2            = ${attr2}
									      ,pt.attr3            = ${attr3}
									      ,pt.attr4            = ${attr4}
									      ,pt.attr5            = ${attr5}
									      ,pt.attr6            = ${attr6}
									      ,pt.attr7            = ${attr7}
									      ,pt.attr8            = ${attr8}
									      ,pt.attr9            = ${attr9}
									      ,pt.attr10           = ${attr10}
									      ,pt.modid            = ${userId}
									      ,pt.moddate          = TO_CHAR(SYSDATE,'YYYYMMDD')
									      ,pt.modtime          = TO_CHAR(SYSDATE,'HH24MISS')   
            WHEN NOT MATCHED THEN
                 INSERT 
                       ( pt.company_cd
					      ,pt.po_no
					      ,pt.tax_seq
					      ,pt.wt_type
					      ,pt.wt_cd
					      ,pt.wt_rate
					      ,pt.wt_base_amt
					      ,pt.wt_tax_amt 
					      ,pt.attr1
					      ,pt.attr2
					      ,pt.attr3
					      ,pt.attr4
					      ,pt.attr5
					      ,pt.attr6
					      ,pt.attr7
					      ,pt.attr8
					      ,pt.attr9
					      ,pt.attr10 
					      ,pt.regid
					      ,pt.regdate
					      ,pt.regtime
					      )values(
					       ${companyCd}             
					      ,${poNo}
					      ,${taxSeq}
					      ,${wtType}
					      ,${wtCd}
					      ,${wtRate}
					      ,${wtBaseAmt}
					      ,${wtTaxAmt} 
					      ,${attr1}
					      ,${attr2}
					      ,${attr3}
					      ,${attr4}
					      ,${attr5}
					      ,${attr6}
					      ,${attr7}
					      ,${attr8}
					      ,${attr9}
					      ,${attr10}
					      ,${userId}
					      ,TO_CHAR(SYSDATE,'YYYYMMDD')
					      ,TO_CHAR(SYSDATE,'HH24MISS') 
					      )


DELETE FROM tb_po_tax
 WHERE company_cd = ${companyCd}
   AND po_no      = ${poNo}
