statements.statement:

        	INSERT INTO TB_CEMENT_PROD_MASTER(
	        	COMPANY_CD
	        	,SYSKEY
	        	,PROD_DATE
	        	,POST_DATE
	        	,CEMENT_QTY
	        	,STATUS
	        	,REGID
				,REGDATE
				,REGTIME
			)
	        VALUES(
	          	 ${companyCd}
				,${syskey}
				,${prodDate}
				,${prodDate}
				,${cementQty}
				,${status}
                ,${userId}
                ,TO_CHAR(SYSDATE,'YYYYMMDD')
                ,TO_CHAR(SYSDATE,'HH24MISS')
        	)


        	INSERT INTO TB_CEMENT_PROD_OUTPUT(
				COMPANY_CD
				,SYSKEY
				,MATER_SEQ
				,MATER_CD
				,PROD_OUT_QTY
				,STOR_LOCT
				,ATTR1
				,PLANT_CD
				,REGID
				,REGDATE
				,REGTIME
        	) 
        	VALUES(
	          	 ${companyCd}
				,${syskey}
				,${materSeq}
				,${materCd}
				,${prodOutQty}
				,${storLoct}
				,${attr1}
				,'GA10'
                ,${userId}
                ,TO_CHAR(SYSDATE,'YYYYMMDD')
                ,TO_CHAR(SYSDATE,'HH24MISS')
        	)


        	INSERT INTO TB_CEMENT_PROD_INPUT(
				COMPANY_CD
				,SYSKEY
				,MATER_SEQ
				,MATER_CD
				,PROD_IN_QTY
				,STOR_LOCT
				,ATTR1
				,PLANT_CD
				,REGID
				,REGDATE
				,REGTIME
        	) 
        	VALUES(
	          	 ${companyCd}
				,${syskey}
				,${materSeq}
				,${materCd}
				,${prodInQty}
				,${storLoct}
				,${attr1}
				,'GA10'
                ,${userId}
                ,TO_CHAR(SYSDATE,'YYYYMMDD')
                ,TO_CHAR(SYSDATE,'HH24MISS')
        	)


UPDATE TB_CEMENT_PROD_OUTPUT
   SET PROD_OUT_QTY = ${prodOutQty},
       ATTR1        = ${attr1},
       MODID        = ${userId},
       MODDATE      = TO_CHAR(SYSDATE,'YYYYMMDD'),
       MODTIME      = TO_CHAR(SYSDATE,'HH24MISS')
 WHERE COMPANY_CD   = ${companyCd}
   AND MATER_SEQ    = ${materSeq}
   AND SYSKEY       = ${syskey}


DELETE FROM TB_CEMENT_PROD_OUTPUT
 WHERE COMPANY_CD   = ${companyCd}
   AND MATER_SEQ    = ${materSeq}
   AND SYSKEY       = ${syskey}


UPDATE TB_CEMENT_PROD_INPUT
   SET PROD_IN_QTY  = ${prodInQty},
       ATTR1        = ${attr1},
       MODID        = ${userId},
       MODDATE      = TO_CHAR(SYSDATE,'YYYYMMDD'),
       MODTIME      = TO_CHAR(SYSDATE,'HH24MISS')
 WHERE COMPANY_CD   = ${companyCd}
   AND MATER_SEQ    = ${materSeq}
   AND SYSKEY       = ${syskey}



   SELECT STATUS
 	  FROM TB_CEMENT_PROD_MASTER
