			INSERT INTO TB_CEMENT_SALES_DETAIL
					(COMPANY_CD
					,DEPT_CD
					,SALES_DATE
					,SALES_NO
					,SALES_ITEM_SEQ
					,MATER_CD
					,TOTAL_AMT
					,UNIT_PRICE
					,SALES_QTY
					,SALES_AMT
					,VAT_CD
					,VAT_AMT
					,UNIT
					,STOR_LOCT
					,REGID
					,REGDATE
					,REGTIME)
			VALUES 
					(${companyCd}
					,${deptCd}
					,REPLACE(${salesDate},'/','')
					,${salesNo}
					,( SELECT LPAD(TO_NUMBER(NVL(MAX(SALES_ITEM_SEQ),0))+10,6,0) 
						  FROM TB_CEMENT_SALES_DETAIL 
						 WHERE COMPANY_CD = ${companyCd}
						   AND SALES_DATE = REPLACE(${salesDate},'/','')
						   AND SALES_NO   = ${salesNo})
					,${materCd}
				  ,${totalAmt}
				  ,${unitPrice}
				  ,${salesQty}
				  ,${salesAmt}
				  ,${vatCd}
				  ,${vatAmt}
				  ,${unit}
				  ,${storLoct}
				  ,${userId}
 				  ,TO_CHAR(SYSDATE,'YYYYMMDD')
				  ,TO_CHAR(SYSDATE,'HH24MISS')
				  )


DELETE TB_CEMENT_SALES_MASTER
 WHERE COMPANY_CD  = ${companyCd}
   AND SALES_DATE  = REPLACE(${salesDate},'/','')
   AND SALES_NO    = ${salesNo}


DELETE TB_CEMENT_SALES_DETAIL
 WHERE COMPANY_CD  = ${companyCd}
   AND SALES_DATE  = REPLACE(${salesDate},'/','')
   AND SALES_NO    = ${salesNo}


DELETE TB_CEMENT_SALES_DETAIL
 WHERE COMPANY_CD     = ${companyCd}
   AND SALES_DATE     = REPLACE(${salesDate},'/','')
   AND SALES_NO       = ${salesNo}
   AND SALES_ITEM_SEQ = ${salesItemSeq}

SELECT '0' chk ,ce_mas.sales_no ,(SELECT vend_mas.vend_nm_en FROM tb_vend_master vend_mas WHERE vend_mas.company_cd = ${companyCd} and vend_mas.vend_cd = ce_mas.customer_cd) customer ,ce_mas.customer_cd ,to_char(to_date(ce_mas.sales_date,'yyyymmdd'),'yyyy/mm/dd') sales_date ,ce_mas.grand_total_qty ,ce_mas.currency_cd ,ce_mas.grand_total_amt ,ce_mas.pay_term ,lgiusf.F_GETCOMMNAME('COMM',${companyCd},${lang},'2319',ce_mas.status) status_nm ,ce_mas.status ,ce_mas.bl_if_doc_seq ,(SELECT attr10 FROM lgiusf.tb_comm_cd_detail comm WHERE comm.company_cd = ce_mas.company_cd AND group_cd = '2001' AND detail_cd = ce_mas.dept_cd) group_cd ,ce_mas.sap_seq ,ce_mas.sap_rtn_msg ,ce_mas.trans_date ,(SELECT NVL(cop_det.vat_cd, 'A1') FROM tb_cement_sales_detail cop_det WHERE cop_det.company_cd = ce_mas.company_cd AND cop_det.sales_date = ce_mas.sales_date AND cop_det.sales_no = ce_mas.sales_no AND rownum=1) vat_cd FROM tb_cement_sales_master ce_mas WHERE ce_mas.company_cd = ${companyCd} AND ce_mas.sales_date >= replace(${salesDateFrom},'/','') AND ce_mas.sales_date <= replace(${salesDateTo},'/','') AND original_sales_no IS NULL {#1} {#2}
AND ce_mas.customer_cd like '%'||${vendorCd}||'%'
AND ce_mas.status like '%'||${status}||'%'

SELECT '0' chk ,ce_mas.sales_no ,ce_mas.original_sales_no ,(SELECT vend_mas.vend_nm_en FROM tb_vend_master vend_mas WHERE vend_mas.company_cd = ${companyCd} and vend_mas.vend_cd = ce_mas.customer_cd) customer ,ce_mas.customer_cd ,to_char(to_date(ce_mas.sales_date,'yyyymmdd'),'yyyy/mm/dd') sales_date ,to_char(to_date(ce_mas.doc_date,'yyyymmdd'),'yyyy/mm/dd') doc_date ,to_char(to_date(ce_mas.posting_date,'yyyymmdd'),'yyyy/mm/dd') posting_date ,ce_mas.grand_total_qty ,ce_mas.currency_cd ,ce_mas.memo_type ,ce_mas.grand_total_amt ,ce_mas.pay_term ,ce_mas.original_amt ,ce_mas.condition_value ,ce_mas.condition_vat_amt ,ce_mas.condition_vat_cd ,ce_mas.condition_total_amt ,lgiusf.F_GETCOMMNAME('COMM',${companyCd},${lang},'2320',ce_mas.status) status_nm ,ce_mas.status ,ce_mas.attr1 org_billing_no ,(SELECT attr10 FROM lgiusf.tb_comm_cd_detail comm WHERE comm.company_cd = ce_mas.company_cd AND group_cd = '2001' AND detail_cd = ce_mas.dept_cd) group_cd ,ce_mas.sap_rtn_msg ,ce_mas.sap_seq ,ce_mas.trans_date FROM tb_cement_sales_master ce_mas WHERE ce_mas.company_cd = ${companyCd} AND ce_mas.sales_date >= replace(${salesDateFrom},'/','') AND ce_mas.sales_date <= replace(${salesDateTo},'/','') AND original_sales_no IS NOT NULL {#1} {#2}
AND ce_mas.customer_cd like '%'||${vendorCd}||'%'
AND ce_mas.status like '%'||${status}||'%'


DELETE tb_cement_sales_master
 WHERE company_cd = ${companyCd}
   AND sales_date  = replace(${salesDate},'/','')
