SELECT '0' chk ,ce_mas.sales_no ,(SELECT vend_mas.vend_nm_en FROM tb_vend_master vend_mas WHERE vend_mas.company_cd = ${companyCd} and vend_mas.vend_cd = ce_mas.customer_cd) customer ,ce_mas.customer_cd ,ce_mas.sales_date ,ce_mas.regdate ,ce_mas.grand_total_qty ,ce_mas.currency_cd ,ce_mas.grand_total_amt ,ce_mas.pay_term ,lgiusf.F_GETCOMMNAME('COMM',${companyCd},${lang},'2319',ce_mas.status) status_nm ,ce_mas.status ,ce_mas.bl_if_doc_seq ,(SELECT attr10 FROM lgiusf.tb_comm_cd_detail comm WHERE comm.company_cd = ce_mas.company_cd AND group_cd = '2001' AND detail_cd = ce_mas.dept_cd) group_cd ,ce_mas.sap_seq ,ce_mas.sap_rtn_msg ,ce_mas.trans_date ,to_DATE( ce_mas.regdate,'YYYYMMDD')- to_DATE(ce_mas.sales_date,'YYYYMMDD') credate_doc_term ,CASE WHEN TO_DATE( ce_mas.regdate,'YYYYMMDD')-TO_DATE(ce_mas.sales_date,'YYYYMMDD')>5 THEN 'TRUE' ELSE 'FALSE' END as term ,CASE WHEN TO_DATE( ce_mas.regdate,'YYYYMMDD')-TO_DATE(ce_mas.sales_date,'YYYYMMDD')>5 THEN 'CPO Sales Input delayed.' END as message FROM tb_cement_sales_master ce_mas WHERE ce_mas.company_cd = ${companyCd} AND ce_mas.sales_date >= replace(${salesDateFrom},'/','') AND ce_mas.sales_date <= replace(${salesDateTo},'/','') AND original_sales_no IS NULL {#1} {#2}
AND ce_mas.customer_cd like '%'||${vendorCd}||'%'
AND ce_mas.status like '%'||${status}||'%'
